import { useState, useEffect, useRef } from 'react'

function Graph() { 

    const canvasRef = useRef(null);

    const [verticalLines, setVerticalLines] = useState([]);
    const [dataPoints, setDataPoints] = useState([Array.from({length: 20}, () => [0, 0, 0])]);

    function addVeriticalLine(event) { 

        const canvas = canvasRef.current;
        const ctx = canvas.getContext("2d");

        const rect = canvas.getBoundingClientRect();
        const x = event.clientX - rect.left;


        setVerticalLines(prevLines => {
            const newLines = [...prevLines, x].sort((a, b) => a - b);
            updatePointIds(newLines);
            return newLines;
        });

    }


    function updatePointIds(verticalLines) { 
        const canvas = canvasRef.current;
        const x_step = canvas.width / (dataPoints.length - 1);

        const updatedPoints = dataPoints.map((point, index) => { 
            const x = x_step * index; // x pos on graph
            let newId = 0;

            for (let i = 0; i < verticalLines.length; i++) {
                if (x > verticalLines[i]) { 
                    newId = i + 1;
                }
                else { 
                    break;
                }
            }

            return [point[0], newId, point[2]];
        });

        setDataPoints(updatedPoints);

    }


    function drawGraph() {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext("2d");

        const x_step = canvas.width / (dataPoints.length - 1);

        ctx.clearRect(0, 0, canvas.width, canvas.height);
        ctx.beginPath();
        ctx.moveTo(0, dataPoints[0][0]);

        ctx.strokeStyle = "blue";
        ctx.lineWidth = 2;

        // draw datapoints graph
        dataPoints.forEach((point, index) => { 
            const x = index * x_step;
            const price = point[0];
            const color = point[2];

            if (color == 0) {
                ctx.strokeStyle = "blue";
            }
            else if (color == 1) {
                ctx.strokeStyle = "red";
            }

            ctx.lineTo(x, price);
            ctx.stroke();
        });


        // draw vertical lines
        verticalLines.forEach(line => {

            ctx.beginPath();
            ctx.moveTo(line, 0);
            ctx.lineTo(line, canvas.height);
            ctx.strokeStyle = "black";
            ctx.linewidth = 2;
            ctx.stroke();
        })
    }


    // populate array with data
    useEffect(() => { 
        const canvas = canvasRef.current;
        const initialData = Array.from({length: 20}, () => [Math.random() * canvas.height, 0, 0])
        setDataPoints(initialData);
    }, []);


    // draw graph
    useEffect(() => { 
        console.log("detected a change in dataPoints");
        drawGraph();
    }, [verticalLines, dataPoints]);


    // add event listener to graph
    useEffect(() => { 
        const canvas = canvasRef.current;
        canvas.addEventListener("click", addVeriticalLine);

        return () => { 
            canvas.removeEventListener("click", addVeriticalLine);
        }
    }, []);


    function handleClick() {
        console.log(dataPoints);
    }



    return (
        <>
            <canvas id="graph" ref={canvasRef} width="800" height="400"></canvas>
            <button onClick={handleClick}>Log</button>
        </>
    )
}

export default Graph;








