import { useEffect } from 'react'

function Graph() { 

    function drawVeriticalLine(event) { 
        const canvas = document.getElementById("graph");
        const ctx = canvas.getContext("2d");

        const rect = canvas.getBoundingClientRect();
        const x = event.clientX - rect.left;

        ctx.beginPath();
        ctx.moveTo(x, 0);
        ctx.lineTo(x, canvas.height);
        ctx.strokeStyle = "black";
        ctx.linewidth = 2;
        ctx.stroke();
    }


    useEffect(() => { 
        const canvas = document.getElementById("graph");
        const ctx = canvas.getContext('2d');

        const dataPoints = Array.from({length: 20}, () => [Math.random() * canvas.height, 0, 0]);

        ctx.clearRect(0, 0, canvas.width, canvas.height);
        ctx.beginPath();
        ctx.moveTo(0, dataPoints[0][0]);

        dataPoints.forEach((y, index) => { 
            const x = (index / (dataPoints.length - 1)) * canvas.width;
            const price = point[0]
            ctx.lineTo(x, price);
        });

        ctx.strokeStyle = "blue";
        ctx.lineWidth = 2; 
        ctx.stroke();

    }, []);


    useEffect(() => { 
        const canvas = document.getElementById("graph");
        canvas.addEventListener("click", drawVeriticalLine);

        return () => { 
            canvas.removeEventListener("click", drawVeriticalLine);
        }
    }, []);



    return (
        <>
            <canvas id="graph" width="800" height="400"></canvas>
        </>
    )
}

