import { useEffect } from 'react'

function Graph() { 

    useEffect(() => { 
        const canvas = document.getElementById("graph");
        const ctx = canvas.getContext('2d');

        const dataPoints = Array.from({length: 20}, () => Math.random() * canvas.height);

        ctx.clearRect(0, 0, canvas.width, canvas.height);
        ctx.beginPath();
        ctx.moveTo(0, dataPoints[0]);

        dataPoints.forEach((y, index) => { 
            const x = (index / (dataPoints.length - 1)) * canvas.width;
            ctx.lineTo(x, y);
        });

        ctx.strokeStyle = "blue";
        ctx.lineWidth = 2; 
        ctx.stroke();

    }, []);


    useEffect(() => {
        const canvas = document.getElementById("graph");
        const ctx = canvas.getContext('2d');

        const handleClick = (event) => {
            const rect = canvas.getBoundingClientRect();
            const x = event.clientX - rect.left;

            // Draw a vertical black line at the clicked position
            ctx.beginPath();
            ctx.moveTo(x, 0);
            ctx.lineTo(x, canvas.height);
            ctx.strokeStyle = "black";
            ctx.lineWidth = 2;
            ctx.stroke();
        };

        canvas.addEventListener('click', handleClick);

        // Cleanup function to remove the event listener
        return () => {
            canvas.removeEventListener('click', handleClick);
        };
    }, []);



    return (
        <>
            <canvas id="graph" width="800" height="400"></canvas>
        </>
    )
}

export default Graph;
