function Graph() { 

    const canvas = document.getElementById("graph");
    const ctx = canvas.getContext("2d");

    // Example data
    const stockData = [100, 105, 102, 110, 108, 115, 112, 120, 125, 130];

    // Basic drawing function
    function drawStockChart(data) {
        ctx.clearRect(0, 0, canvas.width, canvas.height); // Clear canvas
        ctx.beginPath();

        const stepX = canvas.width / data.length;
        const maxPrice = Math.max(...data);
        const minPrice = Math.min(...data);

        data.forEach((price, index) => {
            const x = index * stepX;
            const y = ((maxPrice - price) / (maxPrice - minPrice)) * canvas.height;

            if (index === 0) {
                ctx.moveTo(x, y);
            } else {
                ctx.lineTo(x, y);
            }
        });

        ctx.strokeStyle = "#00f";
        ctx.lineWidth = 2;
        ctx.stroke();
    }

    drawStockChart(stockData);

    return (
        <>
            <canvas id="graph" width="800" height="400"></canvas>
        </>
    )
}

export default Graph;
