import { useState, useEffect, useRef } from 'react'

function Graph() { 

    const canvasRef = useRef(null);

    const [verticalLines, setVerticalLines] = useState([]);
    const [dataPoints, setDataPoints] = useState(Array.from({length: 20}, () => [0, 0, 0]));

    function drawVeriticalLine(event) { 
        const canvas = canvasRef.current;
        const ctx = canvas.getContext("2d");

        const rect = canvas.getBoundingClientRect();
        const x = event.clientX - rect.left;

        ctx.beginPath();
        ctx.moveTo(x, 0);
        ctx.lineTo(x, canvas.height);
        ctx.strokeStyle = "black";
        ctx.linewidth = 2;
        ctx.stroke();

        setVerticalLines(prevLines => [...prevLines, x]);
    }


    // populate array with data
    useEffect(() => { 
        const canvas = canvasRef.current;

        setDataPoints(Array.from({length: 20}, () => [Math.random() * canvas.height, 0, 0]))


    }, []);


    // populate array with data
    useEffect(() => { 
        const canvas = canvasRef.current;
        const ctx = canvas.getContext("2d");

        ctx.clearRect(0, 0, canvas.width, canvas.height);
        ctx.beginPath();
        ctx.moveTo(0, dataPoints[0][0]);

        dataPoints.forEach((point, index) => { 
            const x = (index / (dataPoints.length - 1)) * canvas.width;
            const price = point[0]
            ctx.lineTo(x, price);
        });

        ctx.strokeStyle = "blue";
        ctx.lineWidth = 2; 
        ctx.stroke();

    }, []);



    useEffect(() => { 
        const canvas = canvasRef.current;
        canvas.addEventListener("click", drawVeriticalLine);

        return () => { 
            canvas.removeEventListener("click", drawVeriticalLine);
        }
    }, []);


    function handleClick() {
        console.log(verticalLines);
    }



    return (
        <>
            <canvas id="graph" ref={canvas} width="800" height="400"></canvas>
            <button onClick={handleClick}>Log</button>
        </>
    )
}

export default Graph;








